import React, { useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col, Alert } from 'react-bootstrap';


const CreateRecipe = () => {


  // const navigate = useNavigate();
  // !isUserLoggedIn() && navigate('/about')

  const [title, setTitle] = useState('');
  const [instructions, setInstructions] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [prepTime, setPrepTime] = useState('');
  const [categoryId, setCategoryId] = useState('');
  const [error, setError] = useState('');
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    // Fetch categories from the backend API
    fetch('/categories')
      .then(response => response.json())
      .then(data => setCategories(data.data));
  }, []);

  const handleSubmit = event => {
    event.preventDefault();

    // Send a POST request to create the new recipe
    fetch('http://localhost:3000/recipes', {
      method: 'POST',
      crossorigin: true,
      mode: "no-cors",
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        title,
        ingredients,
        instructions,
        prepTime,
        category_id: ca
    })
      .then(response => response.json())
      .then(data => (data.data))
  });


  // const handleSubmit = async (event) => {
  //   event.preventDefault();

  //   try {
  //     const response = await fetch('/recipes', {
  //       method: 'POST',
  //       headers: {
  //         // 'Authorization': `Bearer ${token}`,
  //         'Content-Type': 'application/json',

  //       },
  //       body: JSON.stringify({
  //         title,
  //         instructions,
  //         ingredients,
  //         prep_time: prepTime,
  //         categoryId: categoryId,

  //       })
  //     });
  //     const data = await response.json();
  //     console.log(data);
  // // redirect to login page after successful registration
  //   } catch (error) {
  //     setError(error.message);
  //   }
  // };

  return (
    <Container>
      <Row className="justify-content-md-center mt-5">
        <Col xs={12} md={8}>
          <h2 className="text-center mb-4">Create a Recipe</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formBasicTitle">
              <Form.Label>Title</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter title"
                value={title}
                onChange={(event) => setTitle(event.target.value)}
              />
            </Form.Group>

            <Form.Group controlId="formBasicInstructions">
              <Form.Label>Instructions</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Enter instructions"
                value={instructions}
                onChange={(event) => setInstructions(event.target.value)}
              />
            </Form.Group>

            <Form.Group controlId="formBasicIngredients">
              <Form.Label>Ingredients</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Enter ingredients"
                value={ingredients}
                onChange={(event) => setIngredients(event.target.value)}
              />
            </Form.Group>

            <Form.Group controlId="formBasicPrepTime">
            <br></br>
              <Form.Label>Preparation Time (in minutes)</Form.Label>
              <Form.Control
                type="number"
                placeholder="Enter preparation time"
                value={prepTime}
                onChange={(event) => setPrepTime(event.target.value)}
              />
            </Form.Group>

            <Form.Group controlId="formBasicCategoryId">
              <br></br>
              <Form.Label>Category</Form.Label>
              <Form.Control
                as="select"
                value={categoryId}
                onChange={(event) => setCategoryId(event.target.value)}
              >
                 <option value="0">Select a category...</option>
            {categories.map(category => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
              </Form.Control>
            </Form.Group>

            <Button variant="primary" type="submit">
              Create Recipe
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};
}
export default CreateRecipe;
